const carsData=[{id:1,name:"Aston Martin",modifications:["Suspension Upgrades","Turbochargers and Superchargers","Sport Seats"],maxSpeed:320,photo:"img/aston_martin.webp"},{id:2,name:"Audi R8",modifications:["Paint Jobs","Window Tint","Nitrous Oxide"],maxSpeed:350,photo:"img/audi_r8.webp"},{id:3,name:"Bugatti Veyron",modifications:["Nitrous Oxide","Suspension Upgrades","Paint Jobs"],maxSpeed:330,photo:"img/buggati.webp"},{id:4,name:"Corvette ZR1",modifications:["Underbody Neon Lights","Racing Harnesses","Catalytic Converter"],maxSpeed:470,photo:"img/corvette.webp"},{id:5,name:"Dodge Viper",modifications:["Racing Harnesses","Excessive Ride Height Adjustments"],maxSpeed:390,photo:"img/dodge_viper.webp"},{id:6,name:"Koenigsegg Regera",modifications:["Nitrous Oxide","Racing Harnesses","Paint Jobs"],maxSpeed:290,photo:"img/regera.webp"}];function modifications(i){return`\n  <h4 class="car__modificationstitle">Modifications</h4>\n  <ul class="car__modification">\n  ${i.map((i=>`<li>${i}</li>`)).join("")}\n  </ul>\n  `}function carTemplate(i){return`\n      <div class="car" id = "${i.id}">\n      <img class="car__image" src="${i.photo}">\n      <h2 class="car__name">${i.name} </h2>\n      <p><strong>Maximum speed :</strong> ${i.maxSpeed}</p>\n      ${modifications(i.modifications)}\n      <div class="button">\n      <button class="button__edit" id = "button_edit${i.id}">Edit</button>\n      <button class="button__delete" id = "button_delete${i.id}">Delete</button>\n      </div>\n      </div>\n    `}document.getElementById("app").innerHTML=`\n    <h1 class="app-title">Cars </h1>\n    ${carsData.map(carTemplate).join("")}\n  `;for(let i of carsData)$(`#button_edit${i.id}`).click((function(){let e=$(`#${i.id}`).is(".editable");$(`#${i.id}`).prop("contenteditable",!e).toggleClass("editable"),e?$(`#button_edit${i.id}`).html("Edit"):$(`#button_edit${i.id}`).html("Save")}));for(let i of carsData)$(`#button_delete${i.id}`).click((function(){$(`#${i.id}`).remove()}));